import streamlit as stimport streamlit.components.v1 as components#st.button(test())## The different variable needed#initaivl concentration of ethanolconc_ini=st.slider('Initial ethanol concentration in %', value=60,step=5)#vol_init=st.slider('Volume after filtration in cL',10,100,value=70)conc_finale=st.slider('Final ethanol concentration wished in % ',                            min_value=10,                            max_value=60,                            step=5,                            value=30)sugar_conc=st.slider('Sugar in g/L',                     200,                     300,                     step=10,                     value=250)if conc_finale > conc_ini:    st.warning('check your concentration')else:    vol_final = (conc_ini * vol_init / conc_finale)    vol_to_add =  vol_final - vol_init        weight_sugar= vol_final * sugar_conc /100    st.write('You need to add a sirop of {0} cL with {1} g of sugar'.format(int(vol_to_add),int(weight_sugar)))def main():    html_temp =""" <div class='tableauPlaceholder' id='viz1630083016755' style='position: relative'><noscript><a href='#'><img alt='31,05 Dashboard  ' src='https:&#47;&#47;public.tableau.com&#47;static&#47;images&#47;pi&#47;pint21YouTube-dashboard31_05&#47;3105Dashboard&#47;1_rss.png' style='border: none' /></a></noscript><object class='tableauViz'  style='display:none;'><param name='host_url' value='https%3A%2F%2Fpublic.tableau.com%2F' /> <param name='embed_code_version' value='3' /> <param name='site_root' value='' /><param name='name' value='pint21YouTube-dashboard31_05&#47;3105Dashboard' /><param name='tabs' value='no' /><param name='toolbar' value='yes' /><param name='static_image' value='https:&#47;&#47;public.tableau.com&#47;static&#47;images&#47;pi&#47;pint21YouTube-dashboard31_05&#47;3105Dashboard&#47;1.png' /> <param name='animate_transition' value='yes' /><param name='display_static_image' value='yes' /><param name='display_spinner' value='yes' /><param name='display_overlay' value='yes' /><param name='display_count' value='yes' /><param name='language' value='en-GB' /></object></div>                <script type='text/javascript'>                    var divElement = document.getElementById('viz1630083016755');                    var vizElement = divElement.getElementsByTagName('object')[0];                    if ( divElement.offsetWidth > 800 ) { vizElement.style.width='100%';vizElement.style.height=(divElement.offsetWidth*0.75)+'px';} else if ( divElement.offsetWidth > 500 ) { vizElement.style.width='100%';vizElement.style.height=(divElement.offsetWidth*0.75)+'px';} else { vizElement.style.width='100%';vizElement.style.height='1177px';}                     var scriptElement = document.createElement('script');                    scriptElement.src = 'https://public.tableau.com/javascripts/api/viz_v1.js';                    vizElement.parentNode.insertBefore(scriptElement, vizElement);                </script>"""    components.html(html_temp)if __name__ == "__main__":        main()