import streamlit as st#st.button(test())## The different variable needed#initaivl concentration of ethanolconc_ini=st.slider('Initial ethanol concentration in %', value=60,step=5)#vol_init=st.slider('Volume after filtration in cL',10,100,value=70)conc_finale=st.slider('Final ethanol concentration wished in % ',                            min_value=10,                            max_value=60,                            step=5,                            value=30)sugar_conc=st.slider('Sugar in g/L',                     200,                     300,                     step=10,                     value=250)if conc_finale > conc_ini:    st.warning('check your concentration')else:    vol_final = (conc_ini * vol_init / conc_finale)    vol_to_add =  vol_final - vol_init        weight_sugar= vol_final * sugar_conc /100    st.write('You need to add a sirop of {0} cL with {1} g of sugar'.format(int(vol_to_add),int(weight_sugar)))